import React, {Component} from 'react';
import './App.css';
import Navigation from './Navigation';
import axios from 'axios';

const API_KEY = process.env.REACT_APP_YELP_API_KEY;
// console.log(API_KEY);
// const config = {
//     headers: {
//         'Authorization': `Bearer ${API_KEY}`
//     },
//     params: {
//         term: '',
//         location: ''
//     }
// };

// const Todo = props => (
//   <tr>
//       <td >{props}</td>
//       <td >{props}</td>
//       <td >{props}</td>

//   </tr>
// )

const Business = props =>(
  <div>
    <p>id: {props.id}</p>
    <p>name: {props.name}</p>
  </div>

);
class App extends Component{

  constructor(props){
    super(props);
    this.state={
      keyword: '',
      location:'',
      business:[],
      list:[],
      businessLen:0
    }
    this.onChangeKeyword = this.onChangeKeyword.bind(this);
    this.onChangeLocation = this.onChangeLocation.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {  

    }

    // need to add food background image 
    onChangeKeyword(e){
      this.setState({
        keyword: e.target.value
      })
    }

    onChangeLocation(e){
      this.setState({
        location: e.target.value
      })
    }
    handleSubmit(e){
      e.preventDefault(); 
      const config = {
        headers: {
            'Authorization': `Bearer ${API_KEY}`
        },
        params: {
            term: this.state.keyword,
            location: this.state.location
        }
      };

      axios.get('https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search', config)
       .then(
           response => {
            //  console.log(response.data);
            this.setState({
              business: response.data
            }); 
           //need to use callback function here
          //  const lists = response.data;
            // this.callback(response.data.businesses);
            const lists = response.data.businesses;
            console.log(response.data)//list of business, region ...
            console.log(lists)//list of 20 business only 
            this.setState({businessLen: lists.length});
            for(var i=0; i <lists.length; i++){
              // console.log(data[i].name);
              console.log(lists[i]);
              this.state.list.push(lists[i]);
              return (
                <p>{lists[i].id}</p>
              )
            }
            //console.log(this.state.list)//it works here
          }

         );

    }


    callback(data){
      console.log(data);
      for(var i=0; i <data.length; i++){
        // console.log(data[i].name);
        // this.state.list.push(data[i].name);
        // return(
        //   <p>{data[i].name}</p>
        // )
        this.businessList();

      }
    }
    // businessList(){
    //   // console.log(this.state.list)
    //   // for(var i = 0; i< this.state.businessLen ; i++){
    //   //   console.log(this.state.list.i);
    //   // }
    //   console.log(this.state.businessLen);
    //   return this.state.list.map(function(currentBusiness, i){

    //     return <Business business={currentBusiness} key={i} />;
    //   });
    // }

    businessList(){
      const displayList = this.state.list;
      console.log(displayList)
      const listt= displayList.map(item => {
        return (
          <div>
            <p>displayList.name</p>
          </div>
        )
      });
    }



    // todoList() {
    //   return this.state.business.map(function(currentTodo, i){
    //       return <Todo todo={currentTodo} key={i} />;
    //   })
 // }


  render(){
    // const list = this.state.business.map(item => {
    //   return (
    //     <p>item</p>
    //   )
    // });
    return(
      <div className="App">

        <Navigation/>

        {/* add search bar  */}
        <div className="container-fluid">
        <p>Search Form</p>
        <form className="form-inline" onSubmit={this.handleSubmit}>


          {/* <label class="sr-only" for="inlineFormInputGroupUsername2">Find</label> */}
          <div className="input-group mb-2 mr-sm-2">
            <div className="input-group-prepend">
              <div className="input-group-text">Find</div>
            </div>
            <input type="text" 
              className="form-control" 
              id="inlineFormInputGroupUsername2" 
              value={this.state.keyword}
              onChange={this.onChangeKeyword}
              placeholder="Do Something" />
          </div>
          {/* <label class="sr-only" for="inlineFormInputGroupUsername2">Location</label> */}
          <div className="input-group mb-2 mr-sm-2">
            <div className="input-group-prepend">
              <div className="input-group-text">Location</div>
            </div>
            <input type="text" 
              className="form-control" 
              id="inlineFormInputGroupUsername2" 
              value={this.state.location}
              onChange={this.onChangeLocation}
              placeholder="Zip code or City"
               />
          </div>
          <button type="submit" className="btn btn-info mb-2">Search</button>
        </form>

        <div>
          <p>hello</p>
        {this.businessList()}
        </div>



        </div>

      </div>
    )

  }
}

export default App;
